import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  foods: any[] = [
    { name: 'Banana', calories: 105, image: 'https://example.com/banana.jpg', quantity: 1 },
    { name: 'Salmon', calories: 367, image: 'https://example.com/salmon.jpg', quantity: 1 },
    { name: 'Cake', calories: 225, image: 'https://example.com/cake.jpg', quantity: 1 }
    // Add more initial foods as needed
  ];

  newFood: any = { name: '', calories: 0, image: '', quantity: 1 };
  showForm: boolean = false;
  searchTerm: string = '';
  todaysFoods: any[] = [];

  // Toggle the form for adding new foods
  toggleForm() {
    this.showForm = !this.showForm;
  }

  // Add a new food to the list
  addFood() {
    if (this.newFood.name && this.newFood.calories && this.newFood.image) {
      this.foods.push({ ...this.newFood });
      this.newFood = { name: '', calories: 0, image: '', quantity: 1 };
      this.showForm = false;
    }
  }

  // Filter foods based on the search term
  get filteredFoods() {
    return this.foods.filter(food => food.name.toLowerCase().includes(this.searchTerm.toLowerCase()));
  }

  // Add a food to today's foods
  addToTodaysFoods(food: any) {
    const existingFood = this.todaysFoods.find(f => f.name === food.name);
    if (existingFood) {
      existingFood.quantity += food.quantity;
    } else {
      this.todaysFoods.push({ ...food });
    }
  }

  // Calculate the total calories for today's foods
  calculateTotalCalories() {
    return this.todaysFoods.reduce((total, food) => total + food.calories * food.quantity, 0);
  }
}

<div>
  <h1>Food List</h1>

  <!-- Task 3: Add New Foods -->
  <button (click)="toggleForm()">Add Food</button>
  <div *ngIf="showForm">
    <h2>Add New Food</h2>
    <form (submit)="addFood()">
      <label for="foodName">Name:</label>
      <input id="foodName" type="text" [(ngModel)]="newFood.name" required>

      <label for="foodCalories">Calories:</label>
      <input id="foodCalories" type="number" [(ngModel)]="newFood.calories" required>

      <label for="foodImage">Image URL:</label>
      <input id="foodImage" type="text" [(ngModel)]="newFood.image" required>

      <button type="submit">Submit</button>
    </form>
  </div>

  <!-- Task 2: Search Foods -->
  <label for="search">Search:</label>
  <input id="search" type="text" [(ngModel)]="searchTerm">

  <!-- Task 1: Display Foods -->
  <ul>
    <li *ngFor="let food of filteredFoods">
      <img [src]="food.image" alt="{{ food.name }}" width="100" height="100">
      <p>{{ food.name }}</p>
      <p>Calories: {{ food.calories }}</p>
      <!-- Task 5 (Bonus): Quantities -->
      <label for="quantity">Quantity:</label>
      <input id="quantity" type="number" [(ngModel)]="food.quantity" (change)="addToTodaysFoods(food)">
      <!-- Task 4: Today's Foods List -->
      <button (click)="addToTodaysFoods(food)">Add to Today's List</button>
    </li>
  </ul>

  <!-- Task 4: Today's Foods List -->
  <h2>Today's Foods List</h2>
  <ul>
    <li *ngFor="let todaysFood of todaysFoods">
      {{ todaysFood.name }} x{{ todaysFood.quantity }}
    </li>
  </ul>

  <p>Total Calories for Today: {{ calculateTotalCalories() }}</p>
</div>
