import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  foods = [
    { name: 'Banana', calories: 105, image: 'banana.jpg' },
    { name: 'Salmon', calories: 367, image: 'salmon.jpg' },
    { name: 'Cake', calories: 450, image: 'cake.jpg' }
  ];

  searchTerm = '';
  showForm = false;
  newFood: any = { name: '', calories: 0, image: '' };
  todayFoods: any[] = [];
  totalCalories = 0;

  toggleForm() {
    this.showForm = !this.showForm;
  }

  addFood() {
    this.foods.push({ ...this.newFood, quantity: 1 });
    this.newFood = { name: '', calories: 0, image: '' };
    this.showForm = false;
  }

  addToToday(food: any) {
    const index = this.todayFoods.findIndex(item => item.name === food.name);
    if (index === -1) {
      this.todayFoods.push({ ...food });
    } else {
      this.todayFoods[index].quantity++;
    }
    this.totalCalories += food.calories;
  }

  removeFromToday(food: any) {
    const index = this.todayFoods.findIndex(item => item.name === food.name);
    if (index !== -1) {
      if (this.todayFoods[index].quantity > 1) {
        this.todayFoods[index].quantity--;
      } else {
        this.todayFoods.splice(index, 1);
      }
      this.totalCalories -= food.calories;
    }
  }
}

<div id="headerContainer">
  <h1>Today's Foods</h1>
  <div id="searchBox">
    <input type="text" [(ngModel)]="searchTerm" placeholder="Search by name...">
  </div>
</div>

<app-food-list [foods]="foods" [searchTerm]="searchTerm"></app-food-list>

<div id="newFood">
  <button (click)="toggleForm()">Add New Food</button>
  <div id="displayContainer">
    <div *ngIf="showForm">
      <h2>Add New Food</h2>
      <form (submit)="addFood()">
        <label for="name">Name:</label>
        <input type="text" id="name" [(ngModel)]="newFood.name" required>
        <br>

        <label for="calories">Calories:</label>
        <input type="number" id="calories" [(ngModel)]="newFood.calories" required>
        <br>

        <label for="image">Image URL:</label>
        <input type="text" id="image" [(ngModel)]="newFood.image" required>
        <br>

        <button type="submit">Add Food</button>
      </form>
    </div>
  </div>
</div>

<div id="todayFoods">
  <h2>Today's Foods</h2>
  <ul>
    <li *ngFor="let food of todayFoods">
      {{ food.name }} x{{ food.quantity }}
      <button (click)="removeFromToday(food)">Remove</button>
    </li>
  </ul>
  <p>Total Calories: {{ totalCalories }}</p>
</div>
